import React, { useEffect, useRef } from 'react';
import './Services.css';

const Services = () => {
  const servicesRef = useRef();

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            const cards = entry.target.querySelectorAll('.service-card');
            cards.forEach((card, index) => {
              setTimeout(() => {
                card.classList.add('animate-in');
              }, index * 100);
            });
          }
        });
      },
      { threshold: 0.1 }
    );

    if (servicesRef.current) {
      observer.observe(servicesRef.current);
    }

    return () => observer.disconnect();
  }, []);

  const servicesData = [
    {
      icon: 'fas fa-rocket',
      title: 'Fullstack Prototyping',
      description: 'Rapid development of AI-powered applications with modern tech stacks. From concept to deployment in weeks.',
      features: ['AI Integration', 'Scalable Architecture', 'Cloud Deployment']
    },
    {
      icon: 'fas fa-lightbulb',
      title: 'Strategic Consulting',
      description: 'Expert guidance on AI strategy, technology selection, and implementation roadmaps for your business.',
      features: ['AI Strategy Development', 'Technology Assessment', 'Implementation Planning']
    },
    {
      icon: 'fas fa-graduation-cap',
      title: 'Workforce Education',
      description: 'Comprehensive training programs to upskill your team in AI technologies and best practices.',
      features: ['AI Fundamentals', 'Hands-on Workshops', 'Certification Programs']
    },
    {
      icon: 'fas fa-sitemap',
      title: 'Architecture Design',
      description: 'Scalable and robust system architecture designed for AI workloads and future growth.',
      features: ['System Design', 'Performance Optimization', 'Security & Compliance']
    },
    {
      icon: 'fas fa-database',
      title: 'Data Engineering',
      description: 'End-to-end data pipeline development for AI model training and production deployment.',
      features: ['Data Pipeline Design', 'MLOps Implementation', 'Model Monitoring']
    },
    {
      icon: 'fas fa-users',
      title: 'Team Augmentation',
      description: 'Expert AI engineers and consultants to bolster your development team and accelerate projects.',
      features: ['Senior AI Engineers', 'Project Management', 'Knowledge Transfer']
    }
  ];

  return (
    <section id="services" className="services" ref={servicesRef}>
      <div className="container">
        <div className="section-header">
          <h2>Our Services</h2>
          <p>Comprehensive AI solutions to accelerate your digital transformation</p>
        </div>
        <div className="services-grid">
          {servicesData.map((service, index) => (
            <div key={index} className="service-card">
              <div className="service-icon">
                <i className={service.icon}></i>
              </div>
              <h3>{service.title}</h3>
              <p>{service.description}</p>
              <ul>
                {service.features.map((feature, featureIndex) => (
                  <li key={featureIndex}>{feature}</li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Services;
