import React, { useEffect } from 'react';
import './Hero.css';

const Hero = () => {
  useEffect(() => {
    const elements = document.querySelectorAll('.floating-element');
    elements.forEach((el, index) => {
      el.style.animationDelay = `${index * 2}s`;
    });
  }, []);

  const scrollToContact = () => {
    const element = document.getElementById('contact');
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const scrollToServices = () => {
    const element = document.getElementById('services');
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <section id="home" className="hero">
      <div className="hero-container">
        <div className="hero-content">
          <div className="title-container">
            <h1 className="hero-title">
              <span className="gradient-text">AI-Powered Solutions</span>
              <span className="title-line-two">for the Future</span>
            </h1>
          </div>
          <div className="subtitle-container">
            <p className="hero-subtitle">
              Transform your business with cutting-edge AI technology. We specialize in fullstack prototyping, 
              strategic consulting, and workforce education to drive innovation.
            </p>
          </div>
          <div className="hero-buttons">
            <button className="btn btn-primary" onClick={scrollToContact}>
              Schedule Free AI Consultation
            </button>
            <button className="btn btn-secondary" onClick={scrollToServices}>
              Learn More
            </button>
          </div>
        </div>
        <div className="hero-visual">
          <div className="floating-elements">
            <div className="floating-element element-1">
              <i className="fas fa-robot"></i>
            </div>
            <div className="floating-element element-2">
              <i className="fas fa-code"></i>
            </div>
            <div className="floating-element element-3">
              <i className="fas fa-chart-line"></i>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
